mapper.select:
       SELECT *
		FROM member_tb
		WHERE mb_id=#{mb_id}
		AND mb_pw=#{mb_pw}

       SELECT *
		FROM member_tb
		WHERE mb_id=#{mb_id} AND mb_pw = #{mb_pw}

       SELECT *
		FROM member_tb
		WHERE mb_id=#{mb_id} AND mb_pw = #{mb_pw}

mapper.update:
UPDATE member_tb
SET
 mb_pw = #{mb_pw}
,mb_name = #{mb_name}
,mb_nickname = #{mb_nickname}
,mb_email = #{mb_email}
,mb_addr = #{mb_addr}
,mb_lineid = #{mb_lineid}
WHERE mb_id = #{mb_id}
